import React, {Component} from 'react'
import {Table, Checkbox, Button} from 'semantic-ui-react'
import TodoItem from './TodoItem'
import styled from 'styled-components'

fetch('http://localhost:4500')
.then(data => data.json())
.then(data => console.log({data}))
.catch(err => console.log({err}))

const NewTodo = styled.input`
    width: 100%;
    height: 50px;
    padding: 15px;
    border-radius: 10px;
    border-color: #7fe0fd;
    font-size: 1.5em;
`;

class TodoApp extends Component {
  state = {
    todos : [

    ],
    newTodo: ''
  }

  handleTodoClick(todo, index) {
    const {completed} = todo
    const [...todos] = this.state.todos
    todos[index] = {...todo, completed: !completed,}
    this.setState({todos})
  }

  handleToggleAll = () => {
    const [...todos] =this.state.todos
    const allToggled = todos.every(todo => todo.completed)
    const toggledTodos = todos.map(todo => ({
      ...todo,
      completed: !allToggled,
    }))
    this.setState({todos: toggledTodos})
  }

  handleClearCompleted = () => {
    const {todos} = this.state
    const incompleteTodos = todos.filter(todo => !todo.completed)
    this.setState({todos: incompleteTodos})
  }


  handleInputChange = event => {
    const value=event.target.value
    this.setState({newTodo: value})
  }

  handleNewTodoKeyDown = event => {
    if (this.state.todos.length >= 10) {
      return //Only allows for 10 inputs
    }

    if (event.keyCode!==13) {
      return //Only add when eneter key is hit
    }

    event.preventDefault()

    const {newTodo, todos} = this.state
    var value = newTodo.trim()
    if (value) {
      this.setState({
        todos: [...todos, { title: value, completed: false}],
        newTodo: ''
      }
    )}
  }

  handleDelete = (i) =>{
    const {todos} = this.state
    const todosLeft = todos.filter(
      (t, index) => index !== i,
    )
    this.setState({
      todos: todosLeft
    })
  }

  render() {
    const {todos} = this.state
    const allToggled = todos.every(todo => todo.completed)
    return (
        <div className='todo-container'>
          <NewTodo
            id='new-todo'
            placeholder='Start a new task?'
            autoFocus
            onChange={this.handleInputChange}
            onKeyDown={this.handleNewTodoKeyDown}
            value = {this.state.newTodo}
          />
          <label
            htmlFor='new-todo'
            style={{display: 'none'}}
          >
            New Todo
          </label>
          {todos.length===0 ? (
            <Table>
              <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>
                      Nothing todo!
                    </Table.HeaderCell>
                </Table.Row>
              </Table.Header>
            </Table>
          ) : <Table color='teal' unstackable={true} celled>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell textAlign='center'>
                  <Checkbox
                    checked = {allToggled}
                    onChange={this.handleToggleAll}
                  />
                </Table.HeaderCell>
                <Table.HeaderCell>
                  Toggle All
                </Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
            {this.state.todos.map((todo, i) => (
              <TodoItem
                key = {i}
                todo={todo}
                handleToggle={() => this.handleTodoClick(todo, i)}
                handleDelete={() => this.handleDelete(i)}
              />
              ))}
            </Table.Body>
            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan='2'>
                  <Button
                    size='small'
                    onClick={this.handleClearCompleted}
                  >
                    Clear completed
                  </Button>
                </Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        }
        </div>
    )
  }
}

export default TodoApp;
